
public class Primitive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean b= true;
		byte c= 10;
		short d= 20;
		char e = 'a';
		int a =1;
		long f = 30;
		float g = 40.0f;
		double h = 80.0d;
		String s= "Vihaan";
		System.out.println(" Display boolean"+ b);
		System.out.println(" Display byte"+ c);
		System.out.println(" Display short"+ d);
		System.out.println(" Display char"+ e);
		System.out.println(" Display int"+ a);
		System.out.println(" Display long"+ f);
		System.out.println(" Display float"+ g);
		System.out.println(" Display double"+ h);
		System.out.println(" Display String"+ s);
		
		
		
	}

}
